import React, { useRef, useEffect } from "react";

const CanvasBg = ({ color }: { color: string }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const context = canvas.getContext("2d");
    if (!context) return;

    const width = canvas.width;
    const height = canvas.height;
    const gridSize = 25; // Ukuran grid dalam piksel

    // Bersihkan canvas
    context.clearRect(0, 0, width, height);

    // Gambar grid
    context.strokeStyle = "#ddd"; // Warna garis grid
    context.lineWidth = 1;

    // Garis vertikal
    for (let x = 0; x <= width; x += gridSize) {
      context.beginPath();
      context.moveTo(x, 0);
      context.lineTo(x, height);
      context.stroke();
    }

    // Garis horizontal
    for (let y = 0; y <= height; y += gridSize) {
      context.beginPath();
      context.moveTo(0, y);
      context.lineTo(width, y);
      context.stroke();
    }

    // Contoh menggambar lingkaran
    context.fillStyle = color;
    context.beginPath();
    context.arc(0, 500, 10, 0, Math.PI * 2, true);
    context.fill();
  }, [color]);

  return (
    <div className="place-self-center">
      <p className="place-sl">ea</p>
      <canvas ref={canvasRef} width={1000} height={500} className="border-2" />
    </div>
  );
};

export default CanvasBg;
