import React, { useState, useRef } from "react";

const App = () => {
  const canvasRef = useRef(null);

  // State untuk parameter
  const [v0, setV0] = useState(50); // Kecepatan awal (default 50 m/s)
  const [angle, setAngle] = useState(45); // Sudut peluncuran (default 45 derajat)
  const [isAnimating, setIsAnimating] = useState(false);

  // Gravitasi
  const g = 9.8;

  // Fungsi untuk menghitung lintasan
  const drawParabola = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");

    // Bersihkan canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Konversi sudut ke radian
    const theta = (angle * Math.PI) / 180;

    // Komponen kecepatan
    const vx = v0 * Math.cos(theta);
    const vy = v0 * Math.sin(theta);

    // Skala waktu untuk animasi
    let t = 0;
    const interval = 0.05; // Interval waktu

    const animate = () => {
      if (!canvas) return;

      // Hitung posisi
      const x = vx * t;
      const y = vy * t - 0.5 * g * t ** 2;

      // Jika y < 0 (tanah), hentikan animasi
      if (y < 0) {
        setIsAnimating(false);
        return;
      }

      // Gambarkan burung
      ctx.beginPath();
      ctx.arc(x, canvas.height - y, 5, 0, Math.PI * 2);
      ctx.fillStyle = "red";
      ctx.fill();
      ctx.closePath();

      // Perbarui waktu
      t += interval;

      // Lanjutkan animasi
      if (isAnimating) {
        requestAnimationFrame(animate);
      }
    };

    animate();
  };

  // Mulai simulasi
  const startSimulation = () => {
    setIsAnimating(true);
    drawParabola();
  };

  // Reset simulasi
  const resetSimulation = () => {
    setIsAnimating(false);
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    ctx.clearRect(0, 0, canvas.width, canvas.height);
  };

  return (
    <div>
      <h1>Simulasi Parabola</h1>
      <div>
        <label>
          Kecepatan Awal (v₀):
          <input type="number" value={v0} onChange={(e) => setV0(Number(e.target.value))} />
        </label>
        <label>
          Sudut Peluncuran (θ):
          <input type="number" value={angle} onChange={(e) => setAngle(Number(e.target.value))} />
        </label>
        <button onClick={startSimulation} disabled={isAnimating}>
          Start
        </button>
        <button onClick={resetSimulation}>Reset</button>
      </div>
      <canvas ref={canvasRef} width="800" height="400" style={{ border: "1px solid black" }} />
    </div>
  );
};

export default App;
