import React, { useRef, useEffect } from "react";
import { ICanvasData } from "../../utility/Type";

const CanvasBg = ({ canvasData }: { canvasData: ICanvasData }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const resistansiUdara = Number((Math.random() * (10 - 5) + 5).toFixed(3));

  const draw = (Vo: string, angle: number, context: CanvasRenderingContext2D, canvas: HTMLCanvasElement) => {
    const Vox = Number(Vo) * Math.cos(angle);
    const Voy = Number(Vo) * Math.sin(angle);
    const g = 9.8;
    const startTime = Date.now();

    const update = () => {
      context.clearRect(0, 0, canvas.width, canvas.height);
      const t = Date.now() - startTime / 100;
      const x = Vox * t;
      const y = Voy * t - (0.5 * g * Math.pow(t, 2));
      context.beginPath();
      context.arc(x, y, 5, 0, Math.PI * 2, true);
      context.fill();
      if (y < 0) return;

      setTimeout(update, t);
    };

    update();
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const context = canvas.getContext("2d");
    if (!context) return;

    const width = canvas.width;
    const height = canvas.height;
    const gridSize = 25; // Ukuran grid dalam piksel

    // Bersihkan canvas
    context.clearRect(0, 0, width, height);

    // Gambar grid
    context.strokeStyle = "#ddd"; // Warna garis grid
    context.lineWidth = 1;

    // Garis vertikal
    for (let x = 0; x <= width; x += gridSize) {
      context.beginPath();
      context.moveTo(x, 0);
      context.lineTo(x, height);
      context.stroke();
    }

    // Garis horizontal
    for (let y = 0; y <= height; y += gridSize) {
      context.beginPath();
      context.moveTo(0, y);
      context.lineTo(width, y);
      context.stroke();
    }

    // Contoh menggambar lingkaran
    context.fillStyle = canvasData.warna;
    draw(canvasData.kecepatan, Number(canvasData.sudut) * (Math.PI / 180), context, canvas);
  }, [canvasData]);

  return (
    <div className="place-self-center">
      <p className="text-end">Resistensi Udara : {resistansiUdara} m/sÂ²</p>
      <canvas ref={canvasRef} width={1000} height={500} className="border-2" />
      <div className="flex items-center justify-between">
        <p>ea</p>
        <p>ea</p>
        <p>ea</p>
      </div>
    </div>
  );
};

export default CanvasBg;
